    float shrink_fact = 1.3;
    int gaps = nums.size(), i;
//This is a comment line.
    bool swapped = true;

    while (gaps > 1 || swapped) {
        gaps = int(float(gaps) / shrink_fact);
        swapped = false;
        i = 0;
        while (gaps + i < nums.size()) {
            if (nums[i] > nums[i + gaps]) {
                std::swap(nums[i], nums[i + gaps]);
                swapped = true;
            }
            i++;
        }
    }
    return nums;
}

