    int first = 0;
    int last = item_list.size() - 1;
    bool found = false;

    while (first <= last && !found) {
        int mid = (first + last) / 2;
        if (item == item_list[mid]) {
            found = true;
        } else {
            if (item < item_list[mid]) {
                last = mid - 1;
            } else {
                first = 1 + mid;
            }
        }
    }

    return found;
}

