class Solution {
public:
    string addBinary(string a, string b) {
        int c=0;
        string s="";
        int i=0;
        while(i<a.length() || i<b.length() || c!=0){
            int x=0;
            if(i<a.length() && a[a.length()-i-1]=='1')
                x=1;
            int y=0;
            if(i<b.length() && b[b.length()-1-i]=='1')
                y=1;
            s=to_string((x+y+c)%2)+s;
            c=(x+y+c)/2;
            i++;
        }
        return s;
    }
};

