    int maxLengthBetweenEqualCharacters(string s) {
        int v[26];
        memset(v,-1,sizeof v);
        int ans=-1;
        for(int i=0;i<s.size();i++){
            if(v[s[i]-'a']==-1){
                v[s[i]-'a']=i;
            }
            int res=i-v[s[i]-'a']-1;
            ans=max(ans,res);
        }
        return ans;
    }
};

