                count++;
            else
                count--;

            if(count==0){

                ans.push_back('1' + makeLargestSpecial(s.substr(i+1,j-i-1)) + '0');
                i = j;
            }
        }

        sort(ans.begin(),ans.end(),greater<string>());
        string finalString = "";
        for(i=0;i<ans.size();i++){
            finalString += ans[i];
        }
        return finalString;
    }
