class Solution {
public:

    bool checker(vector<int> &arr,vector<int> &pref ,vector<int> &suff , int lo,int mid)
    {
        int n= (int)arr.size();
        bool ff =true;
        int hi = lo+mid-1;
        if(lo-1>=0 && pref[lo-1]==0) return false;
        if(hi+1<n && suff[hi+1]==0) return false;
        if(lo-1>=0 && hi+1<n && arr[lo-1]>arr[hi+1] ) return false;
        return true;
    }
    int findLengthOfShortestSubarray(vector<int>& arr) {

       int n = (int)arr.size();
       int ans=n;
       vector<int> pref(n,0),suff(n,0);
       pref[0]=1,suff[n-1]=1;
       for(int i=1;i<n;i++)
       {
           if(arr[i]>=arr[i-1] && pref[i-1]==1) pref[i]=1;
       }

       for(int i=n-2;i>=0;i--)
       {
           if(arr[i]<=arr[i+1] && suff[i+1]==1) suff[i]=1;
       }

       for(int i=0;i<n;i++)
       {
           int lo=0,hi=n;
           while(lo<=hi)
           {
               int mid = (lo+hi)/2;
               if(checker(arr,pref,suff,i,mid))
               {
                   ans = min(ans,mid);
                   hi =mid-1;
               }
               else lo = mid+1;
           }
       }

       return ans;
    }
};
