    int maxSatisfied(vector<int>& customers, vector<int>& grumpy, int minutes) {
       int count = 0; 
       for(int i=0; i<customers.size(); i++) if(grumpy[i] == 0) count+= customers[i]; 
       for(int i=0; i<customers.size(); i++) customers[i] *= grumpy[i]; 
       int max = 0, temp = 0; 
