    int longestWPI(vector<int>& hours) {
    map<int,int> mp;
    int k=0,ans=0;
    for(int i=0;i<hours.size();i++)
    {
        if(hours[i]>8)  k++; else   k--;
        if(k>0)
        {
        ans=max(ans,i+1);
        } else{
            if(mp.find(k-1)!=mp.end()){
                ans=max(ans,i-mp[k-1]);
            }
             if(mp.find(k)==mp.end()){
                mp[k]=i;
            }
        }

    }    
    return ans;
    }
};

