        mp[nums[l]]++;

        while(h<nums.size()){
            mp[nums[h]]++;
            count++;

            if(prev(mp.end())->first - mp.begin()->first <= limit){
                ans=max(ans,count);
            }else{
                while(prev(mp.end())->first - mp.begin()->first > limit && l<h){
                    if(mp[nums[l]]>1)mp[nums[l]]--;
                    else mp.erase(nums[l]);
                    count--;
                    l++;
                }
            }
            h++;
        }
        return ans;
    }
};

