        ans=max(ans,i+1);
        } else{
            if(mp.find(k-1)!=mp.end()){
                ans=max(ans,i-mp[k-1]);
            }
            if(mp.find(k)==mp.end()){
                mp[k+1]=i;
            }
        }

    }    
    return ans;
    }
};

