        // l & o meet twice
        cache[1][2] /= 2;
        cache[1][4] /= 2;

        // find the bottleneck
        int min = Integer.MAX_VALUE;
        for (int frequency: cache[1]) {
            min = Math.min(min, frequency);
        }

        return min;
    }
}

